import React, { useEffect, useState } from "react";
import Webcam from "react-webcam";

const Dashboard = () => {
    const [userData, setUserData] = useState(null);
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        // Fetch User Data from Backend API
        const fetchUserData = async () => {
            try {
                const response = await fetch("http://localhost:5000/user-data");
                const data = await response.json();
                setUserData(data);
            } catch (error) {
                console.error("Error fetching user data:", error);
            }
        };

        fetchUserData();

        // Simulate Productivity Progress
        const interval = setInterval(() => {
            setProgress((prev) => (prev < 100 ? prev + 10 : 100));
        }, 3000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="flex flex-col items-center p-6">
            <h2 className="text-2xl font-bold mb-4">Welcome, {userData?.name || "Guest"}</h2>
            <p className="mb-4">User ID: {userData?.userId}</p>
            <p className="mb-4">Email: {userData?.email}</p>
            <p className="mb-4">Registered At: {userData?.timestamp}</p>

            <div className="w-full max-w-md bg-gray-200 rounded-full">
                <div className="bg-blue-500 text-xs font-medium text-white text-center p-1 leading-none rounded-full" style={{ width: `${progress}%` }}>
                    {progress}%
                </div>
            </div>

            <Webcam className="w-64 h-48 mt-4" />
        </div>
    );
};

export default Dashboard;
