import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import WebcamCapture from "../components/WebcamCapture";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [faceImage, setFaceImage] = useState(null);
  const navigate = useNavigate();

  const handleRegister = () => {
    const userId = `STU${Date.now()}`;
    console.log("User Registered:", { userId, name, email, password, faceImage });
    navigate("/dashboard");
  };

  return (
    <div className="flex flex-col items-center p-6">
      <h2 className="text-2xl font-bold mb-4">Register</h2>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} className="p-2 border rounded w-64" />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="p-2 border rounded w-64" />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="p-2 border rounded w-64" />
      <WebcamCapture onCapture={(img) => setFaceImage(img)} />
      <button onClick={handleRegister} className="mt-4 px-6 py-2 bg-green-500 text-white rounded">Register</button>
    </div>
  );
};

export default Register;
