import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthPage = () => {
    const [isRegister, setIsRegister] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userId, setUserId] = useState('');
    const [image, setImage] = useState(null);
    const webcamRef = useRef(null);
    const navigate = useNavigate();

    const capture = () => {
        const imageSrc = webcamRef.current.getScreenshot();
        setImage(imageSrc);
    };

    const handleAuth = async () => {
        try {
            const url = isRegister ? 'http://localhost:5000/api/register' : 'http://localhost:5000/api/login';
            const data = isRegister
                ? { email, password, image }
                : { userId, password, image };

            const response = await axios.post(url, data);
            alert(response.data.message);
            navigate('/dashboard');
        } catch (error) {
            console.error('Authentication failed:', error);
            alert('Error: ' + error.response?.data?.error || 'Something went wrong');
        }
    };

    return (
        <div className="flex flex-col items-center p-6">
            <h2 className="text-2xl font-bold mb-4">{isRegister ? 'Register' : 'Login'}</h2>
            <div className="flex gap-4 mb-4">
                <button onClick={() => setIsRegister(false)} className="px-4 py-2 bg-blue-500 text-white rounded">Login</button>
                <button onClick={() => setIsRegister(true)} className="px-4 py-2 bg-green-500 text-white rounded">Register</button>
            </div>
            <div className="flex flex-col gap-2">
                {isRegister ? (
                    <>
                        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="p-2 border rounded" />
                        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="p-2 border rounded" />
                    </>
                ) : (
                    <>
                        <input type="text" placeholder="User ID / Email" value={userId} onChange={(e) => setUserId(e.target.value)} className="p-2 border rounded" />
                        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="p-2 border rounded" />
                    </>
                )}
                <Webcam ref={webcamRef} screenshotFormat="image/jpeg" className="w-64 h-48" />
                <button onClick={capture} className="px-4 py-2 bg-gray-500 text-white rounded">Capture Face</button>
            </div>
            <button onClick={handleAuth} className="mt-4 px-6 py-2 bg-purple-500 text-white rounded">{isRegister ? 'Register' : 'Login'}</button>
        </div>
    );
};

export default AuthPage;
