import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import WebcamCapture from "../components/WebcamCapture";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [faceImage, setFaceImage] = useState(null);
  const navigate = useNavigate();

  const handleLogin = () => {
    console.log("Logging in with:", { email, password, faceImage });
    navigate("/dashboard");
  };

  return (
    <div className="flex flex-col items-center p-6">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="p-2 border rounded w-64" />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="p-2 border rounded w-64" />
      <WebcamCapture onCapture={(img) => setFaceImage(img)} />
      <button onClick={handleLogin} className="mt-4 px-6 py-2 bg-blue-500 text-white rounded">Login</button>
    </div>
  );
};

export default Login;
