import React, { useState } from "react";
import WebcamCapture from "../components/WebcamCapture"; // Webcam Component

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = () => {
    console.log("Registering user:", { name, email, password });
  };

  return (
    <div className="flex flex-col items-center p-6">
      <h2 className="text-2xl font-bold mb-4">Register</h2>
      <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} className="p-2 border rounded mb-2" />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="p-2 border rounded mb-2" />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="p-2 border rounded mb-2" />
      <WebcamCapture />
      <button onClick={handleRegister} className="mt-4 px-6 py-2 bg-green-500 text-white rounded">Register</button>
    </div>
  );
};

export default Register;
