import React, { useRef, useState } from "react";
import Webcam from "react-webcam";

const WebcamCapture = ({ onCapture }) => {
  const webcamRef = useRef(null);
  const [image, setImage] = useState(null);

  const capture = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    onCapture(imageSrc);
  };

  return (
    <div className="flex flex-col items-center">
      <Webcam ref={webcamRef} screenshotFormat="image/jpeg" className="w-64 h-48" />
      <button onClick={capture} className="mt-2 px-4 py-2 bg-gray-500 text-white rounded">Capture Face</button>
      {image && <img src={image} alt="Captured" className="mt-2 w-32 h-32 border" />}
    </div>
  );
};

export default WebcamCapture;
